import { createSlice } from "@reduxjs/toolkit";

export const popupCardSlice = createSlice({
  name:'popupCard',
  initialState: {
    value: false
  },
  reducers: {
    openPopupCard: state => {
      state.value = true
    },
    closePopupCard: state => {
      state.value = false
    }
  }
})

export const { openPopupCard, closePopupCard } = popupCardSlice.actions

export default popupCardSlice.reducer
