import { useState } from 'react';
import './App.css';
import ThoughtForm from './components/ThoughtForm';
function App() {
  const [popup, setPopup] = useState(false)
  const popupForm = document.getElementById('thoughtFormContainer')

  const handleToggle = () => {
    setPopup(!popup)
    console.log(popup)
    popup ? popupForm.style.display = 'block' : popupForm.style.display = 'none'
  }

  return (
    <div className='app'>
      <header>
        Your Thoughts
      </header>
      <ThoughtForm  id="thoughtFormContainer"/>
      <button className='addThoughtButton' onClick={handleToggle}>throw thought</button>
    </div>
  );
}

export default App;
