import { createSlice } from "@reduxjs/toolkit";

export const thoughtSlice = createSlice({
  name: 'thought',
  initialState: {
    thoughts: []
  },
  reducers: {
    addThought: (state, action) => {
      state.thoughts.push({
        ...action.payload
      })
    },
    removeThought: (state, action) => {
      return {...state,
        thoughts:state.thoughts.filter(i => i.id !== action.payload.id)}
    },
    togglePopupEdit: (state,action) => {
      return { ...state,
        thoughts: state.thoughts.map((i) => {
          if(i.id === action.payload.id) {
            action.payload.popupEdit = true
          }
        })}
    }
  }
})

export const {addThought, removeThought, togglePopupEdit} = thoughtSlice.actions

export default thoughtSlice.reducer
