import { useSelector, useDispatch } from 'react-redux'
import { closePopup } from '../features/form/popupFormSlice'
import { addThought } from '../features/thought/thoughtSlice'

function ThoughtForm () {
  const dispatch = useDispatch()

  const popupForm = useSelector(state=> state.popupForm.value)

  const handleClick = () => {
    const title = document.getElementById('title').value
    const content = document.getElementById('content').value

    const thought = {
      title,
      content
    }

    dispatch(addThought(thought))

    dispatch(closePopup())
  }
  return (
    <div> 
      {
        popupForm && 
        <div className='bg'>
          <div className="thoughtForm">
            <form className="form">
              <input type='text' id='title' className='inputTitle' placeholder='Title'></input> <br />
              <textarea id='content' className='textareaContent' rows="40" cols="40"></textarea> <br />
            </form>
            <button type='button' onClick={handleClick}>Add</button> <br />
            <button onClick={() => dispatch(closePopup())}>Close</button>
          </div>
        </div>

      }
    </div>
  )
}

export default ThoughtForm
