import './App.css';
import ThoughtForm from './components/ThoughtForm';
import {useDispatch, useSelector} from 'react-redux';
import {openPopup} from './features/form/popupFormSlice'

function App() {
  const dispatch = useDispatch()

  return (
    <div className='app'>
      <header>
        Your Thoughts
      </header>
      <ThoughtForm/>
      <button className='addThoughtButton' onClick={() => dispatch(openPopup())}>throw thought</button>
    </div>
  );
}

export default App;
