import { createSlice } from "@reduxjs/toolkit";

export const thoughtSlice = createSlice({
  name: 'thought',
  initialState: {
    thoughts: []
  },
  reducers: {
    addThought: (state, action) => {
      state.thoughts.push({
        ...action.payload
      })
    },
    removeThought: (state, action) => {
      state.thoughts.filter(i => i.id !== action.payload.id)
    }
  }
})

export const {addThought, removeThought} = thoughtSlice.actions

export default thoughtSlice.reducer
