import {useSelector, useDispatch} from 'react-redux'
import { removeThought } from '../features/thought/thoughtSlice'
import { openPopupEdit } from '../features/thought/thoughtSlice'

function Thoughts() {
  const dispatch = useDispatch()

  const thoughts = useSelector(state => state.thought.thoughts)
  // const popupCardValue = useSelector(state => state.thought.thoughts)

  console.log(thoughts)

  const thoughtItems = thoughts.map((thought, index) => 
    <div key={index} className={thought.popupEdit ? 'a' : 'thoughtCard'} >
      <button className='buttonRemove' onClick={() => dispatch(removeThought({thought, index}))}>x</button>
      <div onClick={() => dispatch(openPopupEdit({thought, index}))}>
        <input value={thought.title}></input>
        <hr />
        <div className='contentContainer'>
          <p>{thought.content}</p>
        </div>
      </div>
    </div>
  )

  return (
    <div className="thoughts">
      {thoughtItems}
    </div>
  )

}

export default Thoughts
