import {useSelector, useDispatch} from 'react-redux'
import { removeThought } from '../features/thought/thoughtSlice'
import { openPopupCard } from '../features/popup/popupCardSlice'


function Thoughts() {
  const dispatch = useDispatch()

  const thoughts = useSelector(state => state.thought.thoughts)
  const popupCardValue = useSelector(state => state.popupCard.value)

  const handleClick = (index) => {
    alert(index)
  }

  const thoughtItems = thoughts.map((thought, index) => 
    <div key={index} className={popupCardValue ? 'a' : 'thoughtCard'} onClick={() => dispatch(openPopupCard())}>
      <button className='buttonRemove' onClick={() => dispatch(removeThought(thought))}>x</button>
      <h3>{thought.title}</h3>
      <hr />
      <div className='contentContainer'>
        <p>{thought.content}</p>
      </div>
    </div>
  )

  return (
    <div className="thoughts">
      {thoughtItems}
    </div>
  )

}

export default Thoughts
