function ThoughtForm (props) {
  const {popup} = props
  const popupForm = document.getElementById('thoughtFormContainer')

  if(popupForm) {
    popupForm.style.display = 'none'
    popup ? popupForm.style.display = 'block' : popupForm.style.display = 'none'
  }

  return (
    <div className="thoughtForm" id="thoughtFormContainer">
      <form className="form">
        <input type='text'></input> <br />
        <input type='text'></input> <br />
        <input type='submit'></input> <br />
        <input type='submit'></input> <br />
      </form>
    </div>
  )
}

export default ThoughtForm
