import { useState } from 'react';
import './App.css';
import ThoughtForm from './components/ThoughtForm';
function App() {
  const [popup, setPopup] = useState(false)

  const handleToggle = () => {
    setPopup(!popup)
  }

  const popupForm = document.getElementById('thoughtFormContainer')
  if(popup) {
    popupForm.style.display='block'
  }else {
    popupForm.style.display='nonde'

  }
  return (
    <div className='app'>
      <header>
        Your Thoughts
      </header>
      <ThoughtForm/>
      <button className='addThoughtButton' onClick={handleToggle}>throw thought</button>
    </div>
  );
}

export default App;
