import { useSelector, useDispatch } from 'react-redux'
import { closePopup } from '../features/form/popupFormSlice'

function ThoughtForm () {
  const dispatch = useDispatch()
  const popupForm = useSelector(state=> state.popupForm.value)
  const handleClick = () => {
    const title = document.getElementById('title')
    const content = document.getElementById('content')

    console.log(title , '  ', content)
  }
  return (
    <div> 
      {
        popupForm && 
        <div className="thoughtForm">
          <form className="form">
            <input type='text' id='title'></input> <br />
            <input type='textarea' id='content'></input> <br />
            <button onClick={handleClick}>Add</button> <br />
            <input type='submit'></input> <br />
          </form>
          <button onClick={() => dispatch(closePopup())}>Close</button>
        </div>
      }
    </div>
  )
}

export default ThoughtForm
