import './App.css';
import ThoughtForm from './components/ThoughtForm';
function App() {
  const formContainer = document.getElementById('thoughtFormContainer')
  formContainer.style.display = 'block'

  const handleFormPopup = () => {
    formContainer.style.display = 'none'
  }
  return (
    <div className='app'>
      <ThoughtForm/>
      <button className='addThoughtButton' onClick={handleFormPopup}>+</button>
    </div>
  );
}

export default App;
